func (u *AppStripeUpsertBulk) Update(set func(*AppStripeUpsert)) *AppStripeUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(update *sql.UpdateSet) {
		set(&AppStripeUpsert{UpdateSet: update})
	}))
	return u
}

