func (u *GrantUpsertBulk) Update(set func(*GrantUpsert)) *GrantUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(update *sql.UpdateSet) {
		set(&GrantUpsert{UpdateSet: update})
	}))
	return u
}

