// PlanAddonCreateBulk is the builder for creating many PlanAddon entities in bulk.
type PlanAddonCreateBulk struct {
	config
	err      error
	builders []*PlanAddonCreate
	conflict []sql.ConflictOption
}

// Save creates the PlanAddon entities in the database.
func (pacb *PlanAddonCreateBulk) Save(ctx context.Context) ([]*PlanAddon, error) {
	if pacb.err != nil {
		return nil, pacb.err
	}
	specs := make([]*sqlgraph.CreateSpec, len(pacb.builders))
	nodes := make([]*PlanAddon, len(pacb.builders))
	mutators := make([]Mutator, len(pacb.builders))
	for i := range pacb.builders {
		func(i int, root context.Context) {
			builder := pacb.builders[i]
			builder.defaults()
			var mut Mutator = MutateFunc(func(ctx context.Context, m Mutation) (Value, error) {
				mutation, ok := m.(*PlanAddonMutation)
				if !ok {
					return nil, fmt.Errorf("unexpected mutation type %T", m)
				}
				if err := builder.check(); err != nil {
					return nil, err
				}
				builder.mutation = mutation
				var err error
				nodes[i], specs[i] = builder.createSpec()
				if i < len(mutators)-1 {
					_, err = mutators[i+1].Mutate(root, pacb.builders[i+1].mutation)
				} else {
					spec := &sqlgraph.BatchCreateSpec{Nodes: specs}
					spec.OnConflict = pacb.conflict
					// Invoke the actual operation on the latest mutation in the chain.
					if err = sqlgraph.BatchCreate(ctx, pacb.driver, spec); err != nil {
						if sqlgraph.IsConstraintError(err) {
							err = &ConstraintError{msg: err.Error(), wrap: err}
						}
					}
				}
				if err != nil {
					return nil, err
				}
				mutation.id = &nodes[i].ID
				mutation.done = true
				return nodes[i], nil
			})
			for i := len(builder.hooks) - 1; i >= 0; i-- {
				mut = builder.hooks[i](mut)
			}
			mutators[i] = mut
		}(i, ctx)
	}
	if len(mutators) > 0 {
		if _, err := mutators[0].Mutate(ctx, pacb.builders[0].mutation); err != nil {
			return nil, err
		}
	}
	return nodes, nil
}

// SaveX is like Save, but panics if an error occurs.
func (pacb *PlanAddonCreateBulk) SaveX(ctx context.Context) []*PlanAddon {
	v, err := pacb.Save(ctx)
	if err != nil {
		panic(err)
	}
	return v
}

// Exec executes the query.
func (pacb *PlanAddonCreateBulk) Exec(ctx context.Context) error {
	_, err := pacb.Save(ctx)
	return err
}

// ExecX is like Exec, but panics if an error occurs.
func (pacb *PlanAddonCreateBulk) ExecX(ctx context.Context) {
	if err := pacb.Exec(ctx); err != nil {
		panic(err)
	}
}

// OnConflict allows configuring the `ON CONFLICT` / `ON DUPLICATE KEY` clause
// of the `INSERT` statement. For example:
//
//	client.PlanAddon.CreateBulk(builders...).
//		OnConflict(
//			// Update the row with the new values
//			// the was proposed for insertion.
//			sql.ResolveWithNewValues(),
//		).
//		// Override some of the fields with custom
//		// update values.
//		Update(func(u *ent.PlanAddonUpsert) {
//			SetNamespace(v+v).
//		}).
//		Exec(ctx)
func (pacb *PlanAddonCreateBulk) OnConflict(opts ...sql.ConflictOption) *PlanAddonUpsertBulk {
	pacb.conflict = opts
	return &PlanAddonUpsertBulk{
		create: pacb,
	}
}

// OnConflictColumns calls `OnConflict` and configures the columns
// as conflict target. Using this option is equivalent to using:
//
//	client.PlanAddon.Create().
//		OnConflict(sql.ConflictColumns(columns...)).
//		Exec(ctx)
func (pacb *PlanAddonCreateBulk) OnConflictColumns(columns ...string) *PlanAddonUpsertBulk {
	pacb.conflict = append(pacb.conflict, sql.ConflictColumns(columns...))
	return &PlanAddonUpsertBulk{
		create: pacb,
	}
}

// PlanAddonUpsertBulk is the builder for "upsert"-ing
// a bulk of PlanAddon nodes.
type PlanAddonUpsertBulk struct {
	create *PlanAddonCreateBulk
}

// UpdateNewValues updates the mutable fields using the new values that
// were set on create. Using this option is equivalent to using:
//
//	client.PlanAddon.Create().
//		OnConflict(
//			sql.ResolveWithNewValues(),
//			sql.ResolveWith(func(u *sql.UpdateSet) {
//				u.SetIgnore(planaddon.FieldID)
//			}),
//		).
//		Exec(ctx)
func (u *PlanAddonUpsertBulk) UpdateNewValues() *PlanAddonUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWithNewValues())
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(s *sql.UpdateSet) {
		for _, b := range u.create.builders {
			if _, exists := b.mutation.ID(); exists {
				s.SetIgnore(planaddon.FieldID)
			}
			if _, exists := b.mutation.Namespace(); exists {
				s.SetIgnore(planaddon.FieldNamespace)
			}
			if _, exists := b.mutation.CreatedAt(); exists {
				s.SetIgnore(planaddon.FieldCreatedAt)
			}
			if _, exists := b.mutation.PlanID(); exists {
				s.SetIgnore(planaddon.FieldPlanID)
			}
			if _, exists := b.mutation.AddonID(); exists {
				s.SetIgnore(planaddon.FieldAddonID)
			}
		}
	}))
	return u
}

// Ignore sets each column to itself in case of conflict.
// Using this option is equivalent to using:
//
//	client.PlanAddon.Create().
//		OnConflict(sql.ResolveWithIgnore()).
//		Exec(ctx)
func (u *PlanAddonUpsertBulk) Ignore() *PlanAddonUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWithIgnore())
	return u
}

// DoNothing configures the conflict_action to `DO NOTHING`.
// Supported only by SQLite and PostgreSQL.
func (u *PlanAddonUpsertBulk) DoNothing() *PlanAddonUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.DoNothing())
	return u
}

// Update allows overriding fields `UPDATE` values. See the PlanAddonCreateBulk.OnConflict
// documentation for more info.
func (u *PlanAddonUpsertBulk) Update(set func(*PlanAddonUpsert)) *PlanAddonUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(update *sql.UpdateSet) {
		set(&PlanAddonUpsert{UpdateSet: update})
	}))
	return u
}

