// UpdateNewValues updates the mutable fields using the new values that
// were set on create. Using this option is equivalent to using:
//
//	client.SubscriptionPhase.Create().
//		OnConflict(
//			sql.ResolveWithNewValues(),
//			sql.ResolveWith(func(u *sql.UpdateSet) {
//				u.SetIgnore(subscriptionphase.FieldID)
//			}),
//		).
//		Exec(ctx)
func (u *SubscriptionPhaseUpsertBulk) UpdateNewValues() *SubscriptionPhaseUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWithNewValues())
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(s *sql.UpdateSet) {
		for _, b := range u.create.builders {
			if _, exists := b.mutation.ID(); exists {
				s.SetIgnore(subscriptionphase.FieldID)
			}
			if _, exists := b.mutation.Namespace(); exists {
				s.SetIgnore(subscriptionphase.FieldNamespace)
			}
			if _, exists := b.mutation.CreatedAt(); exists {
				s.SetIgnore(subscriptionphase.FieldCreatedAt)
			}
			if _, exists := b.mutation.SubscriptionID(); exists {
				s.SetIgnore(subscriptionphase.FieldSubscriptionID)
			}
			if _, exists := b.mutation.Key(); exists {
				s.SetIgnore(subscriptionphase.FieldKey)
			}
			if _, exists := b.mutation.ActiveFrom(); exists {
				s.SetIgnore(subscriptionphase.FieldActiveFrom)
			}
		}
	}))
	return u
}

// Ignore sets each column to itself in case of conflict.
